using System;\nusing System.IO;\nusing System.Windows;\n\nnamespace XmlViewerWpf\n{\n    public partial class MainWindow : Window\n    {\n        public MainWindow()\n        {\n            InitializeComponent();\n            LoadXmlIntoBrowser();\n        }\n\n        private void LoadXmlIntoBrowser()\n        {\n            string xmlPath = "path/to/your/document.xml";\n            string cssPath = "path/to/your/style.css";\n            \n            string xmlContent = File.ReadAllText(xmlPath);\n            string cssContent = File.ReadAllText(cssPath);\n\n            string htmlContent = $@"\n                <!DOCTYPE html>\n                <html>\n                <head>\n                    <style>\n                        {cssContent}\n                    </style>\n                </head>\n                <body>\n                    {xmlContent}\n                </body>\n                </html>";\n\n            webBrowser.NavigateToString(htmlContent);\n        }\n    }\n}