using System;\nusing System.DirectoryServices;\n\npublic class IIS6Manager\n{\n    public void StartAppPool(string appPoolName)\n    {\n        DirectoryEntry appPools = new DirectoryEntry("IIS://localhost/W3SVC/AppPools");\n        DirectoryEntry appPool = appPools.Children.Find(appPoolName, "IIsApplicationPool");\n        appPool.Invoke("Start", null);\n    }\n\n    public void StopAppPool(string appPoolName)\n    {\n        DirectoryEntry appPools = new DirectoryEntry("IIS://localhost/W3SVC/AppPools");\n        DirectoryEntry appPool = appPools.Children.Find(appPoolName, "IIsApplicationPool");\n        appPool.Invoke("Stop", null);\n    }\n\n    public void RecycleAppPool(string appPoolName)\n    {\n        DirectoryEntry appPools = new DirectoryEntry("IIS://localhost/W3SVC/AppPools");\n        DirectoryEntry appPool = appPools.Children.Find(appPoolName, "IIsApplicationPool");\n        appPool.Invoke("Recycle", null);\n    }\n}\n\npublic class IIS6WebsiteManager\n{\n    public void StartWebsite(string siteName)\n    {\n        DirectoryEntry site = new DirectoryEntry($"IIS://localhost/W3SVC/{siteName}");\n        site.Invoke("Start", null);\n    }\n\n    public void StopWebsite(string siteName)\n    {\n        DirectoryEntry site = new DirectoryEntry($"IIS://localhost/W3SVC/{siteName}");\n        site.Invoke("Stop", null);\n    }\n\n    public void RecycleWebsite(string siteName)\n    {\n        StopWebsite(siteName);\n        StartWebsite(siteName);\n    }\n}