using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string headerFilePath = "header.txt";\n        string contentFilePath = "content.txt";\n        string footerFilePath = "footer.txt";\n        string outputFilePath = "output.txt";\n\n        using (var output = new FileStream(outputFilePath, FileMode.Create, FileAccess.Write))\n        {\n            ConcatenateFile(headerFilePath, output);\n            ConcatenateFile(contentFilePath, output);\n            ConcatenateFile(footerFilePath, output);\n        }\n    }\n\n    static void ConcatenateFile(string inputFilePath, FileStream output)\n    {\n        using (var input = new FileStream(inputFilePath, FileMode.Open, FileAccess.Read))\n        {\n            input.CopyTo(output);\n        }\n    }\n}