protected void Page_Load(object sender, EventArgs e)\n{\n    // Ensure this check is done after Page.Validate() is called, typically during a postback event\n    if (IsPostBack)\n    {\n        Page.Validate();\n        \n        if (!Page.IsValid)\n        {\n            foreach (BaseValidator validator in Page.Validators)\n            {\n                if (!validator.IsValid)\n                {\n                    // Apply your custom style to the control being validated\n                    Control controlToStyle = Page.FindControl(validator.ControlToValidate);\n                    if (controlToStyle != null)\n                    {\n                        // Example: Adding a CSS class to highlight the invalid control\n                        controlToStyle.CssClass += " invalid-control";\n                    }\n                }\n            }\n        }\n    }\n}