using System;\nusing System.Data.SQLite;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Data Source=state.db;Version=3;";\n        using (var connection = new SQLiteConnection(connectionString))\n        {\n            connection.Open();\n            using (var command = new SQLiteCommand(connection))\n            {\n                command.CommandText = "CREATE TABLE IF NOT EXISTS ObjectState (Id INTEGER PRIMARY KEY, State TEXT)";\n                command.ExecuteNonQuery();\n\n                command.CommandText = "INSERT INTO ObjectState (State) VALUES (@state)";\n                command.Parameters.AddWithValue("@state", "SampleState");\n                command.ExecuteNonQuery();\n\n                command.CommandText = "SELECT * FROM ObjectState";\n                using (var reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        Console.WriteLine($"Id: {reader["Id"]}, State: {reader["State"]}");\n                    }\n                }\n            }\n        }\n    }\n}