using System;\nusing Microsoft.Web.Administration;\n\nnamespace ApplicationPoolRecycling\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string appPoolName = "YourAppPoolName"; // Replace with your Application Pool name\n\n            using (ServerManager serverManager = new ServerManager())\n            {\n                ApplicationPool appPool = serverManager.ApplicationPools[appPoolName];\n                if (appPool != null)\n                {\n                    appPool.Recycle();\n                    Console.WriteLine($"Application Pool '{appPoolName}' has been recycled.");\n                }\n                else\n                {\n                    Console.WriteLine($"Application Pool '{appPoolName}' not found.");\n                }\n            }\n        }\n    }\n}