using System;\nusing System.Data.SqlClient;\n\npublic void UpdateMonthInDatabase(int newMonth, int recordId)\n{\n    string connectionString = "your_connection_string";\n    string updateQuery = "UPDATE YourTable SET YourDateColumn = DATEADD(month, @NewMonth - MONTH(YourDateColumn), YourDateColumn) WHERE RecordId = @RecordId";\n\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        SqlCommand command = new SqlCommand(updateQuery, connection);\n        command.Parameters.AddWithValue("@NewMonth", newMonth);\n        command.Parameters.AddWithValue("@RecordId", recordId);\n\n        connection.Open();\n        command.ExecuteNonQuery();\n    }\n}