using System;\nusing System.Text.RegularExpressions;\n\npublic class Program\n{\n    public static bool IsPlainText(string input)\n    {\n        if (string.IsNullOrEmpty(input))\n        {\n            return true;\n        }\n\n        // Regular expression to identify HTML tags\n        string pattern = @"<[^>]+>";\n        Regex regex = new Regex(pattern, RegexOptions.Compiled);\n        \n        // Check if the input string contains any HTML tags\n        return !regex.IsMatch(input);\n    }\n\n    public static void Main()\n    {\n        string test1 = "This is a plain text.";\n        string test2 = "This is a <b>bold</b> text.";\n\n        Console.WriteLine(IsPlainText(test1)); // Output: True\n        Console.WriteLine(IsPlainText(test2)); // Output: False\n    }\n}