using System;\nusing System.IO;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Security.Authentication;\nusing System.Security.Cryptography.X509Certificates;\nusing System.Threading.Tasks;\n\nclass TlsServer\n{\n    static async Task Main()\n    {\n        var certificate = new X509Certificate2("path/to/your/certificate.pfx", "password");\n        var listener = new TcpListener(IPAddress.Any, 443);\n        listener.Start();\n\n        while (true)\n        {\n            var client = await listener.AcceptTcpClientAsync();\n            ProcessClientAsync(client, certificate);\n        }\n    }\n\n    static async void ProcessClientAsync(TcpClient client, X509Certificate2 certificate)\n    {\n        using (var networkStream = client.GetStream())\n        using (var sslStream = new SslStream(networkStream, false))\n        {\n            await sslStream.AuthenticateAsServerAsync(certificate, clientCertificateRequired: false, enabledSslProtocols: SslProtocols.Tls12, checkCertificateRevocation: true);\n            // Perform read/write operations over sslStream here\n        }\n    }\n}