using System;\nusing System.IO;\nusing System.Net.Sockets;\nusing System.Security.Authentication;\nusing System.Security.Cryptography.X509Certificates;\n\nclass TlsClient\n{\n    static async Task Main()\n    {\n        var hostname = "yourserver.com";\n        using (var client = new TcpClient(hostname, 443))\n        using (var networkStream = client.GetStream())\n        using (var sslStream = new SslStream(networkStream, false, new RemoteCertificateValidationCallback(ValidateServerCertificate), null))\n        {\n            await sslStream.AuthenticateAsClientAsync(hostname, new X509CertificateCollection(), SslProtocols.Tls12, checkCertificateRevocation: true);\n            // Perform read/write operations over sslStream here\n        }\n    }\n\n    static bool ValidateServerCertificate(object sender, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)\n    {\n        if (sslPolicyErrors == SslPolicyErrors.None)\n            return true;\n\n        // Custom validation logic here\n        return false;\n    }\n}