using System;\nusing System.IO;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        string[] fileUrls = \n        {\n            "http://example.com/datafile.csv",  // Replace with your actual data file URL\n            "http://example.com/image1.jpg",   // Replace with your actual image file URLs\n            "http://example.com/image2.png"\n        };\n\n        string localDirectory = @"C:\DownloadedFiles";  // Replace with your desired local directory\n        Directory.CreateDirectory(localDirectory);\n\n        using (HttpClient client = new HttpClient())\n        {\n            foreach (string fileUrl in fileUrls)\n            {\n                await DownloadFileAsync(client, fileUrl, localDirectory);\n            }\n        }\n\n        Console.WriteLine("Files downloaded successfully.");\n    }\n\n    static async Task DownloadFileAsync(HttpClient client, string fileUrl, string localDirectory)\n    {\n        try\n        {\n            Uri uri = new Uri(fileUrl);\n            string fileName = Path.GetFileName(uri.LocalPath);\n            string localFilePath = Path.Combine(localDirectory, fileName);\n            \n            using (HttpResponseMessage response = await client.GetAsync(fileUrl))\n            {\n                response.EnsureSuccessStatusCode();\n                \n                using (Stream contentStream = await response.Content.ReadAsStreamAsync(),\n                       fileStream = new FileStream(localFilePath, FileMode.Create, FileAccess.Write, FileShare.None, 8192, true))\n                {\n                    await contentStream.CopyToAsync(fileStream);\n                }\n            }\n\n            Console.WriteLine($"Downloaded {fileUrl} to {localFilePath}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error downloading {fileUrl}: {ex.Message}");\n        }\n    }\n}