using System;\nusing DataTech.FaxManJr;\n\npublic class FaxReceiver\n{\n    private Modem modem;\n\n    public FaxReceiver()\n    {\n        modem = new Modem();\n        modem.OnFaxReceived += new FaxReceivedEventHandler(OnFaxReceived);\n    }\n\n    private void OnFaxReceived(object sender, FaxReceivedEventArgs e)\n    {\n        string callerId = e.CallerId;\n        string faxFilePath = e.FaxFilePath;\n\n        // Process the fax based on Caller ID\n        RouteFax(callerId, faxFilePath);\n    }\n\n    private void RouteFax(string callerId, string faxFilePath)\n    {\n        // Implement your routing logic here\n        if (callerId == "1234567890")\n        {\n            // Route to specific department or process accordingly\n            Console.WriteLine($"Fax from {callerId} routed to department A. File: {faxFilePath}");\n        }\n        else\n        {\n            // Default processing\n            Console.WriteLine($"Fax from {callerId} routed to general inbox. File: {faxFilePath}");\n        }\n    }\n\n    public void StartReceiving()\n    {\n        modem.Open();\n        Console.WriteLine("Fax receiver started. Waiting for faxes...");\n    }\n\n    public void StopReceiving()\n    {\n        modem.Close();\n        Console.WriteLine("Fax receiver stopped.");\n    }\n}\n\n// Usage\npublic class Program\n{\n    public static void Main()\n    {\n        FaxReceiver faxReceiver = new FaxReceiver();\n        faxReceiver.StartReceiving();\n\n        // Keep the application running to receive faxes\n        Console.ReadLine();\n        faxReceiver.StopReceiving();\n    }\n}