ProcessStartInfo start = new ProcessStartInfo\n{\n    Arguments = "\"" + newTargetFile + "\"" + " " + "\"" + originalFile.FullName + "\"",\n    FileName = filename,\n    WindowStyle = ProcessWindowStyle.Normal,\n    CreateNoWindow = true,\n    UseShellExecute = false,\n    RedirectStandardOutput = true,\n    RedirectStandardError = true\n};\n\nusing (Process proc = Process.Start(start))\n{\n    proc.OutputDataReceived += (sender, e) => \n    {\n        if (!string.IsNullOrEmpty(e.Data))\n        {\n            Console.WriteLine(e.Data); // You can replace this with a debugger output method if needed\n        }\n    };\n\n    proc.ErrorDataReceived += (sender, e) => \n    {\n        if (!string.IsNullOrEmpty(e.Data))\n        {\n            Console.WriteLine("ERROR: " + e.Data); // You can replace this with a debugger output method if needed\n        }\n    };\n\n    proc.BeginOutputReadLine();\n    proc.BeginErrorReadLine();\n    proc.WaitForExit();\n}