using System;\nusing Microsoft.Web.Administration;\n\npublic class IISManager\n{\n    public void StartWebsite(string siteName)\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            if (site != null && site.State != ObjectState.Started)\n            {\n                site.Start();\n            }\n        }\n    }\n\n    public void StopWebsite(string siteName)\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            if (site != null && site.State != ObjectState.Stopped)\n            {\n                site.Stop();\n            }\n        }\n    }\n\n    public void RecycleWebsite(string siteName)\n    {\n        StopWebsite(siteName);\n        StartWebsite(siteName);\n    }\n}