using System;\nusing WMPLib; // Add reference to Windows Media Player COM\n\nnamespace WmpPlaybackSpeedControl\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            WindowsMediaPlayer wmp = new WindowsMediaPlayer();\n            wmp.URL = "path_to_your_media_file";\n            wmp.controls.play();\n\n            // Wait until the media starts playing\n            System.Threading.Thread.Sleep(2000);\n\n            // Set playback speed (1.0 is normal speed, 0.5 is half speed, 2.0 is double speed, etc.)\n            wmp.settings.rate = 1.5;\n\n            Console.WriteLine("Press any key to exit...");\n            Console.ReadKey();\n\n            wmp.controls.stop();\n        }\n    }\n}