using Moq;\n\n   public class MapinfoWrapperTests\n   {\n       [Fact]\n       public void OpenTable_ShouldSendCorrectCommand()\n       {\n           // Arrange\n           var mockMapinfo = new Mock<IMapinfoWrapper>();\n           var path = @"C:\Temp\MyTable.TAB";\n           var expectedCommand = $"OpenTable(\"{path}\")";\n\n           // Act\n           mockMapinfo.Object.OpenTable(path);\n\n           // Assert\n           mockMapinfo.Verify(m => m.OpenTable(It.Is<string>(cmd => cmd == expectedCommand)), Times.Once());\n       }\n   }