using System;\nusing System.ComponentModel;\n\npublic class CustomBindingList<T> : BindingList<T>\n{\n    public void Move(int oldIndex, int newIndex)\n    {\n        if (oldIndex < 0 || oldIndex >= Count || newIndex < 0 || newIndex >= Count)\n        {\n            throw new ArgumentOutOfRangeException();\n        }\n\n        if (oldIndex == newIndex) return;\n\n        T item = this[oldIndex];\n        bool raiseListChangedEvents = RaiseListChangedEvents;\n        RaiseListChangedEvents = false;\n\n        try\n        {\n            RemoveItem(oldIndex);\n            InsertItem(newIndex, item);\n        }\n        finally\n        {\n            RaiseListChangedEvents = raiseListChangedEvents;\n        }\n\n        if (raiseListChangedEvents)\n        {\n            OnListChanged(new ListChangedEventArgs(ListChangedType.ItemMoved, newIndex, oldIndex));\n        }\n    }\n}