using System;\nusing System.Globalization;\nusing System.Windows.Data;\nusing System.Xml;\n\npublic class XmlElementToPartnerIDConverter : IValueConverter\n{\n    public object Convert(object value, Type targetType, object parameter, CultureInfo culture)\n    {\n        if (value is XmlElement xmlElement)\n        {\n            XmlNode partnerIDNode = xmlElement.SelectSingleNode("PartnerID");\n            if (partnerIDNode != null)\n            {\n                return partnerIDNode.InnerText;\n            }\n        }\n        return null;\n    }\n\n    public object ConvertBack(object value, Type targetType, object parameter, CultureInfo culture)\n    {\n        throw new NotImplementedException();\n    }\n}