using System;\n    using System.Runtime.InteropServices;\n\n    public class ManagedWrapper\n    {\n        // Specify the path to the unmanaged DLL\n        [DllImport("UnmanagedClass.dll", CallingConvention = CallingConvention.Cdecl)]\n        public static extern void MyUnmanagedFunction();\n\n        public void CallUnmanagedFunction()\n        {\n            MyUnmanagedFunction();\n        }\n    }