public class MapinfoWrapperTests\n   {\n       [Fact]\n       public void OpenTable_ShouldSendCorrectCommand()\n       {\n           // Arrange\n           var mockMapinfo = new Mock<IMapinfo>();\n           var wrapper = new MapinfoWrapper(mockMapinfo.Object);\n           var path = @"C:\Temp\MyTable.TAB";\n           var expectedCommand = $"OpenTable(\"{path}\")";\n\n           // Act\n           wrapper.OpenTable(path);\n\n           // Assert\n           mockMapinfo.Verify(m => m.Do(It.Is<string>(cmd => cmd == expectedCommand)), Times.Once());\n       }\n   }