using System;\nusing System.Diagnostics;\n\npublic class DllDependencyScanner\n{\n    public static void GetDllDependencies(string filePath)\n    {\n        ProcessStartInfo processStartInfo = new ProcessStartInfo\n        {\n            FileName = "dumpbin",\n            Arguments = $"/DEPENDENTS \"{filePath}\"",\n            RedirectStandardOutput = true,\n            UseShellExecute = false,\n            CreateNoWindow = true\n        };\n\n        using (Process process = Process.Start(processStartInfo))\n        {\n            using (System.IO.StreamReader reader = process.StandardOutput)\n            {\n                string result = reader.ReadToEnd();\n                Console.WriteLine(result);\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        if (args.Length != 1)\n        {\n            Console.WriteLine("Usage: DllDependencyScanner <path to exe or dll>");\n            return;\n        }\n\n        GetDllDependencies(args[0]);\n    }\n}