protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        ScriptManager.RegisterStartupScript(this, GetType(), "showLoadingModal", "document.getElementById('loadingModal').style.display = 'block';", true);\n    }\n}\n\nprotected void DataBindingMethod()\n{\n    // Your data binding logic here\n\n    // Hide the modal after data binding is done\n    ScriptManager.RegisterStartupScript(this, GetType(), "hideLoadingModal", "document.getElementById('loadingModal').style.display = 'none';", true);\n}