using System.Text.RegularExpressions;\nusing System.Xml;\n\npublic void WriteXml(string inputUserName, string inputEmail)\n{\n    // Example sanitization: remove non-alphanumeric characters\n    string sanitizedUserName = Regex.Replace(inputUserName, @"[^\w\.@-]", "");\n    string sanitizedEmail = Regex.Replace(inputEmail, @"[^\w\.@-]", "");\n\n    using (XmlTextWriter writer = new XmlTextWriter("filename.xml", null))\n    {\n        writer.WriteStartElement("User");\n        \n        writer.WriteElementString("Username", sanitizedUserName);\n        writer.WriteElementString("Email", sanitizedEmail);\n        \n        writer.WriteEndElement();\n    }\n}