using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing HtmlAgilityPack;\n\nnamespace HtmlParserExample\n{\n    class Program\n    {\n        static async Task Main(string[] args)\n        {\n            var url = "http://example.com"; // Replace with the actual URL\n            var httpClient = new HttpClient();\n            var response = await httpClient.GetAsync(url);\n            var pageContents = await response.Content.ReadAsStringAsync();\n\n            var htmlDoc = new HtmlDocument();\n            htmlDoc.LoadHtml(pageContents);\n\n            // Assuming the dropdown has the name attribute "dropdown"\n            var dropdown = htmlDoc.DocumentNode.SelectSingleNode("//select[@name='dropdown']");\n\n            if (dropdown != null)\n            {\n                var optionValue = dropdown.SelectNodes("option")[1].Attributes["value"].Value; // Selecting the 2nd option\n\n                // Creating the post data\n                var postData = new FormUrlEncodedContent(new []\n                {\n                    new KeyValuePair<string, string>("dropdown", optionValue)\n                });\n\n                // Post back to the server\n                var postResponse = await httpClient.PostAsync(url, postData);\n                var postResponseContents = await postResponse.Content.ReadAsStringAsync();\n\n                // Load the post back response HTML\n                var postHtmlDoc = new HtmlDocument();\n                postHtmlDoc.LoadHtml(postResponseContents);\n\n                // Further parsing of the resulting HTML\n                // Example: Selecting a node to get some data\n                var dataNode = postHtmlDoc.DocumentNode.SelectSingleNode("//div[@id='data']");\n\n                if (dataNode != null)\n                {\n                    Console.WriteLine("Data: " + dataNode.InnerText);\n                }\n            }\n            else\n            {\n                Console.WriteLine("Dropdown not found.");\n            }\n        }\n    }\n}