[TestClass]\n    public class UITests\n    {\n        [TestMethod]\n        public void TestButtonClick()\n        {\n            using (var app = Application.Launch("YourApp.exe"))\n            {\n                var window = app.GetWindow("MainWindow");\n                var button = window.Get<Button>("MyButton");\n\n                button.Click();\n\n                var label = window.Get<Label>("MyLabel");\n                Assert.AreEqual("ExpectedText", label.Text);\n            }\n        }\n    }