using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class CustomListView : Form\n{\n    private ListView listView;\n\n    public CustomListView()\n    {\n        listView = new ListView\n        {\n            View = View.Details,\n            OwnerDraw = true,\n            FullRowSelect = true,\n            Dock = DockStyle.Fill\n        };\n\n        listView.Columns.Add("Network", -2, HorizontalAlignment.Left);\n        listView.Columns.Add("Signal Strength", -2, HorizontalAlignment.Left);\n        listView.Columns.Add("Security", -2, HorizontalAlignment.Left);\n\n        listView.Items.Add(new ListViewItem(new[] { "Network 1", "Strong", "WPA2" }));\n        listView.Items.Add(new ListViewItem(new[] { "Network 2", "Medium", "WEP" }));\n        listView.Items.Add(new ListViewItem(new[] { "Network 3", "Weak", "Open" }));\n\n        listView.DrawColumnHeader += ListView_DrawColumnHeader;\n        listView.DrawItem += ListView_DrawItem;\n        listView.DrawSubItem += ListView_DrawSubItem;\n\n        this.Controls.Add(listView);\n    }\n\n    private void ListView_DrawColumnHeader(object sender, DrawListViewColumnHeaderEventArgs e)\n    {\n        e.DrawDefault = true;\n    }\n\n    private void ListView_DrawItem(object sender, DrawListViewItemEventArgs e)\n    {\n        e.DrawDefault = true;\n    }\n\n    private void ListView_DrawSubItem(object sender, DrawListViewSubItemEventArgs e)\n    {\n        e.DrawBackground();\n        e.Graphics.DrawString(e.SubItem.Text, e.SubItem.Font, Brushes.Black, e.Bounds);\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new CustomListView());\n    }\n}