using System.Threading;\nusing System.Threading.Tasks;\n\npublic class ConnectionMonitor\n{\n    private static Timer _timer;\n\n    public static void Start()\n    {\n        _timer = new Timer(CheckConnections, null, TimeSpan.Zero, TimeSpan.FromMinutes(1));\n    }\n\n    private static void CheckConnections(object state)\n    {\n        HeartbeatController.MonitorConnections();\n    }\n}\n\n// Start the connection monitor when the application starts\nprotected void Application_Start()\n{\n    ConnectionMonitor.Start();\n}