string isoXml = "your ISO-8859-1 encoded XML string";\n\n// Convert ISO-8859-1 encoded string to a byte array\nbyte[] isoBytes = Encoding.GetEncoding("ISO-8859-1").GetBytes(isoXml);\n\n// Convert the byte array to a UTF-8 encoded string\nstring utf8Xml = Encoding.UTF8.GetString(isoBytes);\n\nSqlCommand _cmd = new SqlCommand("INSERT INTO dbo.AuditTable(XmlField) VALUES(@XmlContents)", _connection);\n_cmd.Parameters.Add("@XmlContents", SqlDbType.Xml);\n_cmd.Parameters["@XmlContents"].Value = utf8Xml;\n_cmd.ExecuteNonQuery();