public class BizTalkHostSingleton\n{\n    private static readonly Lazy<BizTalkHostSingleton> instance = new Lazy<BizTalkHostSingleton>(() => new BizTalkHostSingleton());\n    private readonly string hostName;\n\n    private BizTalkHostSingleton()\n    {\n        hostName = BizTalkHostHelper.GetCurrentHostName();\n    }\n\n    public static BizTalkHostSingleton Instance\n    {\n        get { return instance.Value; }\n    }\n\n    public string HostName\n    {\n        get { return hostName; }\n    }\n}