using System;\nusing System.Windows;\n\nnamespace YourNamespace\n{\n    public class WpfWindowLauncher\n    {\n        [STAThread]\n        public static void ShowWpfWindow()\n        {\n            // Check if the application is already running\n            if (Application.Current == null)\n            {\n                // Initialize the application if it does not exist\n                var app = new Application();\n                var window = new MyWpfWindow();\n                app.Run(window);\n            }\n            else\n            {\n                // If the application already exists, just create and show the window\n                var window = new MyWpfWindow();\n                window.ShowDialog();\n            }\n        }\n    }\n}