using System.Web.UI;\nusing System.Web.UI.WebControls;\nusing System.Web.UI.WebControls.Adapters;\n\npublic class CustomTreeViewAdapter : ControlAdapter\n{\n    protected override void Render(HtmlTextWriter writer)\n    {\n        TreeView treeView = this.Control as TreeView;\n        if (treeView != null)\n        {\n            writer.Write("<ul>");\n            RenderNodes(treeView.Nodes, writer);\n            writer.Write("</ul>");\n        }\n    }\n\n    private void RenderNodes(TreeNodeCollection nodes, HtmlTextWriter writer)\n    {\n        foreach (TreeNode node in nodes)\n        {\n            writer.Write("<li>");\n            writer.Write(node.Text);\n            if (node.ChildNodes.Count > 0)\n            {\n                writer.Write("<ul>");\n                RenderNodes(node.ChildNodes, writer);\n                writer.Write("</ul>");\n            }\n            writer.Write("</li>");\n        }\n    }\n}