using System;\nusing System.Linq;\nusing System.Data.Linq;\nusing System.Runtime.InteropServices;\nusing Excel = Microsoft.Office.Interop.Excel;\n\nnamespace LinqToExcel\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Initialize Excel application\n            Excel.Application xlApp = new Excel.Application();\n            if (xlApp == null)\n            {\n                Console.WriteLine("Excel is not properly installed!!");\n                return;\n            }\n\n            Excel.Workbook xlWorkBook = xlApp.Workbooks.Add();\n            Excel.Worksheet xlWorkSheet = (Excel.Worksheet)xlWorkBook.Worksheets.get_Item(1);\n\n            // Your LINQ to SQL query\n            var db = new YourDataContext();\n            var data = from d in db.MyTable\n                       where d.Name == "Test"\n                       select d;\n\n            // Populate Excel cells with data\n            int row = 1; // Starting from the first row\n            foreach (var item in data)\n            {\n                xlWorkSheet.Cells[row, 1] = item.Property1; // Assuming Property1 is a column in your table\n                xlWorkSheet.Cells[row, 2] = item.Property2; // Assuming Property2 is another column in your table\n                // Add more columns as necessary\n                row++;\n            }\n\n            // Save the Excel file and cleanup\n            xlWorkBook.SaveAs("C:\\YourPath\\YourExcelFile.xls", Excel.XlFileFormat.xlWorkbookNormal);\n            xlWorkBook.Close(true);\n            xlApp.Quit();\n\n            Marshal.ReleaseComObject(xlWorkSheet);\n            Marshal.ReleaseComObject(xlWorkBook);\n            Marshal.ReleaseComObject(xlApp);\n\n            Console.WriteLine("Excel file created, you can find the file at C:\\YourPath\\YourExcelFile.xls");\n        }\n    }\n}