// Example of an asynchronous method\n   private async Task PerformBackgroundOperationAsync()\n   {\n       await Task.Run(() =>\n       {\n           // Intensive background operation here\n       });\n   }\n\n   // Call the method in a button click handler, for example\n   private async void Button_Click(object sender, RoutedEventArgs e)\n   {\n       await PerformBackgroundOperationAsync();\n   }