using System;\nusing System.Runtime.Remoting;\n\nnamespace RemotingExampleClient\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Obtain a reference to the remote object\n            Server remoteServer = (Server)Activator.GetObject(typeof(Server), "tcp://localhost:8080/MyURL");\n\n            if (remoteServer != null)\n            {\n                string message = remoteServer.GetMessage();\n                Console.WriteLine(message);\n            }\n            else\n            {\n                Console.WriteLine("Could not locate server.");\n            }\n        }\n    }\n}