using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        if (IsDebug())\n        {\n            Console.WriteLine("Assembly is in Debug mode.");\n        }\n        else\n        {\n            Console.WriteLine("Assembly is in Release mode.");\n        }\n    }\n\n    [Conditional("DEBUG")]\n    static void SetDebugMode(ref bool isDebug)\n    {\n        isDebug = true;\n    }\n\n    static bool IsDebug()\n    {\n        bool isDebug = false;\n        SetDebugMode(ref isDebug);\n        return isDebug;\n    }\n}