using Microsoft.AspNetCore.SignalR;\n   using System.Diagnostics;\n\n   public class ConsoleRunner\n   {\n       private readonly IHubContext<ConsoleOutputHub> _hubContext;\n\n       public ConsoleRunner(IHubContext<ConsoleOutputHub> hubContext)\n       {\n           _hubContext = hubContext;\n       }\n\n       public async Task RunConsoleAppAsync()\n       {\n           var processStartInfo = new ProcessStartInfo\n           {\n               FileName = "your-console-app.exe",\n               RedirectStandardOutput = true,\n               UseShellExecute = false,\n               CreateNoWindow = true\n           };\n\n           using (var process = new Process { StartInfo = processStartInfo })\n           {\n               process.OutputDataReceived += async (sender, args) =>\n               {\n                   if (args.Data != null)\n                   {\n                       await _hubContext.Clients.All.SendAsync("ReceiveMessage", args.Data);\n                   }\n               };\n\n               process.Start();\n               process.BeginOutputReadLine();\n               await process.WaitForExitAsync();\n           }\n       }\n   }