using HtmlAgilityPack;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string html = "<p>How to retrieve the text between two html markup with c# ?</p>\n\n<p>Edit : This is the only purpose of my question, 'how to retrieve the string within two html markup using c#', that's all.</p>\n";\n        \n        HtmlDocument doc = new HtmlDocument();\n        doc.LoadHtml(html);\n        \n        foreach (HtmlNode node in doc.DocumentNode.SelectNodes("//p"))\n        {\n            Console.WriteLine(node.InnerText);\n        }\n    }\n}