using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Adjust the DLL name as needed for x86 and x64 versions\n    private const string DllName = "MyDLL.dll";\n\n    [DllImport(DllName)]\n    private static extern int MyFunction();\n\n    static void Main()\n    {\n        try\n        {\n            int result = MyFunction();\n            Console.WriteLine("Function result: " + result);\n        }\n        catch (DllNotFoundException ex)\n        {\n            Console.WriteLine("DLL not found: " + ex.Message);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}