protected void UploadFile_Click(object sender, EventArgs e)\n   {\n       if (FileUpload1.HasFile)\n       {\n           string fileName = FileUpload1.FileName;\n           string contentType = FileUpload1.PostedFile.ContentType;\n           byte[] fileData;\n\n           using (BinaryReader reader = new BinaryReader(FileUpload1.PostedFile.InputStream))\n           {\n               fileData = reader.ReadBytes(FileUpload1.PostedFile.ContentLength);\n           }\n\n           using (SqlConnection conn = new SqlConnection("YourConnectionStringHere"))\n           {\n               conn.Open();\n               using (SqlCommand cmd = new SqlCommand("INSERT INTO Files (FileName, ContentType, FileData) VALUES (@FileName, @ContentType, @FileData)", conn))\n               {\n                   cmd.Parameters.AddWithValue("@FileName", fileName);\n                   cmd.Parameters.AddWithValue("@ContentType", contentType);\n                   cmd.Parameters.AddWithValue("@FileData", fileData);\n                   cmd.ExecuteNonQuery();\n               }\n           }\n       }\n   }