using System;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\nnamespace PDFCreationExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Path where the PDF will be saved\n            string pdfPath = "example.pdf";\n\n            // Create a new document\n            Document document = new Document();\n\n            try\n            {\n                // Create a PdfWriter instance, passing the document and the file stream\n                PdfWriter writer = PdfWriter.GetInstance(document, new FileStream(pdfPath, FileMode.Create));\n\n                // Open the document to enable you to write to it\n                document.Open();\n\n                // Add a title\n                document.AddTitle("Sample PDF Document");\n\n                // Add a paragraph\n                Paragraph paragraph = new Paragraph("Hello, World! This is a sample PDF document created using iTextSharp.");\n                document.Add(paragraph);\n\n                // Add an image (ensure the image path is correct)\n                string imagePath = "path_to_image.jpg";\n                if (File.Exists(imagePath))\n                {\n                    Image img = Image.GetInstance(imagePath);\n                    document.Add(img);\n                }\n\n                // Add another paragraph\n                paragraph = new Paragraph("This is another paragraph.");\n                document.Add(paragraph);\n\n                Console.WriteLine("PDF created successfully at " + pdfPath);\n            }\n            catch (DocumentException de)\n            {\n                Console.Error.WriteLine(de.Message);\n            }\n            catch (IOException ioe)\n            {\n                Console.Error.WriteLine(ioe.Message);\n            }\n            finally\n            {\n                // Close the document\n                document.Close();\n            }\n        }\n    }\n}