using System.Windows.Automation;\nusing TestStack.White;\nusing TestStack.White.UIItems;\nusing TestStack.White.UIItems.Finders;\nusing TestStack.White.UIItems.WindowItems;\nusing TestStack.White.WindowsAPI;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Launch the application\n        var application = Application.Launch("PathToYourApplication.exe");\n        \n        // Get the main window\n        var mainWindow = application.GetWindow("MainWindowTitle", InitializeOption.NoCache);\n\n        // Find and click the button to open the MessageBox\n        var button = mainWindow.Get<Button>("ButtonAutomationIdOrName");\n        button.Click();\n\n        // Give some time for MessageBox to appear\n        System.Threading.Thread.Sleep(1000);\n\n        // Find the MessageBox\n        var messageBox = mainWindow.ModalWindow("Howdy");\n\n        // Verify the content of the MessageBox\n        var messageText = messageBox.Get<Label>(SearchCriteria.ByAutomationId("65535")).Text;\n        if (messageText == "Howdy")\n        {\n            System.Console.WriteLine("MessageBox content is correct.");\n        }\n        else\n        {\n            System.Console.WriteLine("MessageBox content is incorrect.");\n        }\n\n        // Close the MessageBox\n        var okButton = messageBox.Get<Button>(SearchCriteria.ByText("OK"));\n        okButton.Click();\n\n        // Close the application\n        application.Close();\n    }\n}