using (var context = new ApplicationDbContext())\n   {\n       // Create\n       var newProduct = new Product { Name = "Sample Product", Price = 9.99M, Stock = 100 };\n       context.Products.Add(newProduct);\n       context.SaveChanges();\n\n       // Read\n       var product = context.Products.FirstOrDefault(p => p.ProductId == newProduct.ProductId);\n\n       // Update\n       if (product != null)\n       {\n           product.Price = 19.99M;\n           context.SaveChanges();\n       }\n\n       // Delete\n       if (product != null)\n       {\n           context.Products.Remove(product);\n           context.SaveChanges();\n       }\n   }