[TestClass]\n    public class ModuleIntegrationTests\n    {\n        private IUnityContainer _container;\n        private Shell _shell;\n\n        [TestInitialize]\n        public void Setup()\n        {\n            _container = new UnityContainer();\n            _shell = new Shell();\n            \n            // Register services and modules\n            _container.RegisterType<IMyService, MyService>();\n            _container.RegisterInstance(_shell);\n        }\n\n        [TestMethod]\n        public void TestModuleInteraction()\n        {\n            // Arrange\n            var moduleA = _container.Resolve<ModuleA>();\n            var moduleB = _container.Resolve<ModuleB>();\n\n            // Act\n            moduleA.DoSomething();\n            var result = moduleB.CheckSomething();\n\n            // Assert\n            Assert.IsTrue(result);\n        }\n    }