using System.Configuration;\n   using System.Data.Entity;\n\n   public class MyDbContext : DbContext\n   {\n       protected override void OnModelCreating(DbModelBuilder modelBuilder)\n       {\n           string tablePrefix = ConfigurationManager.AppSettings["TablePrefix"] ?? "DefaultPrefix_";\n\n           modelBuilder.Entity<MyEntity>().ToTable($"{tablePrefix}MyEntities");\n           modelBuilder.Entity<AnotherEntity>().ToTable($"{tablePrefix}AnotherEntities");\n\n           // Add similar mapping for other entities\n       }\n   }\n\n   public class MyEntity\n   {\n       public int Id { get; set; }\n       public string Name { get; set; }\n   }\n\n   public class AnotherEntity\n   {\n       public int Id { get; set; }\n       public string Description { get; set; }\n   }