using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    private TextBox textBox1;\n    private TextBox textBox2;\n    private Button submitButton;\n    private ErrorProvider errorProvider;\n\n    public MyForm()\n    {\n        textBox1 = new TextBox { Location = new System.Drawing.Point(20, 20) };\n        textBox2 = new TextBox { Location = new System.Drawing.Point(20, 60) };\n        submitButton = new Button { Text = "Submit", Location = new System.Drawing.Point(20, 100) };\n        errorProvider = new ErrorProvider();\n\n        submitButton.Click += new EventHandler(SubmitButton_Click);\n\n        this.Controls.Add(textBox1);\n        this.Controls.Add(textBox2);\n        this.Controls.Add(submitButton);\n    }\n\n    private void SubmitButton_Click(object sender, EventArgs e)\n    {\n        bool allValid = true;\n\n        // Validate textBox1\n        if (string.IsNullOrWhiteSpace(textBox1.Text))\n        {\n            errorProvider.SetError(textBox1, "This field is required.");\n            allValid = false;\n        }\n        else\n        {\n            errorProvider.SetError(textBox1, string.Empty);\n        }\n\n        // Validate textBox2\n        if (string.IsNullOrWhiteSpace(textBox2.Text))\n        {\n            errorProvider.SetError(textBox2, "This field is required.");\n            allValid = false;\n        }\n        else\n        {\n            errorProvider.SetError(textBox2, string.Empty);\n        }\n\n        if (allValid)\n        {\n            // Proceed with form submission or further processing\n            MessageBox.Show("Form is valid!");\n        }\n        else\n        {\n            MessageBox.Show("Please correct the errors and try again.");\n        }\n    }\n}