using System;\nusing System.Collections.Generic;\nusing System.Text.RegularExpressions;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string input1 = "Some Text here";\n        string input2 = "some text Here";\n        List<string> wordsToMatch = new List<string> { "Some", "Text", "Here" };\n\n        string result1 = AddHtmlMarkup(input1, wordsToMatch);\n        string result2 = AddHtmlMarkup(input2, wordsToMatch);\n\n        Console.WriteLine(result1);\n        Console.WriteLine(result2);\n    }\n\n    public static string AddHtmlMarkup(string input, List<string> wordsToMatch)\n    {\n        foreach (var word in wordsToMatch)\n        {\n            string pattern = $@"\b{Regex.Escape(word)}\b";\n            string replacement = $"<dfn title=\"Definition of {word}\">$&</dfn>";\n            input = Regex.Replace(input, pattern, replacement, RegexOptions.IgnoreCase);\n        }\n        return input;\n    }\n}