using System;\nusing VMwareTasks;\n\nclass Program\n{\n    static void Main()\n    {\n        string vmPath = @"C:\Path\To\Your\VM.vmx";\n        string hostUsername = "your_host_username";\n        string hostPassword = "your_host_password";\n\n        using (VMWareVirtualHost host = new VMWareVirtualHost())\n        {\n            // Connect to the host\n            host.ConnectToVMWareWorkstation();\n            \n            // Open the virtual machine\n            VMWareVirtualMachine vm = host.Open(vmPath);\n            \n            // Power on the virtual machine\n            vm.PowerOn();\n            \n            // Wait for the virtual machine to be powered on\n            vm.WaitForTools();\n            \n            // Log in to the guest operating system\n            vm.LoginInGuest(hostUsername, hostPassword);\n            \n            // Run a program in the guest operating system\n            vm.RunProgramInGuest(@"C:\Path\To\Program.exe", "");\n            \n            // Power off the virtual machine\n            vm.PowerOff();\n        }\n    }\n}