using System;\nusing System.Diagnostics;\nusing System.Threading.Tasks;\n\npublic class Program\n{\n    public static async Task Main(string[] args)\n    {\n        Process msBuild = new Process();\n        msBuild.StartInfo.FileName = "Build.bat";\n        msBuild.StartInfo.UseShellExecute = false;\n        msBuild.StartInfo.RedirectStandardOutput = true;\n        msBuild.StartInfo.RedirectStandardError = true;\n\n        msBuild.OutputDataReceived += (sender, e) =>\n        {\n            if (!string.IsNullOrEmpty(e.Data))\n            {\n                Console.WriteLine(e.Data);\n            }\n        };\n\n        msBuild.ErrorDataReceived += (sender, e) =>\n        {\n            if (!string.IsNullOrEmpty(e.Data))\n            {\n                Console.Error.WriteLine(e.Data);\n            }\n        };\n\n        msBuild.Start();\n        msBuild.BeginOutputReadLine();\n        msBuild.BeginErrorReadLine();\n\n        await msBuild.WaitForExitAsync();\n    }\n}