using System;\nusing System.Net;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            MailAddress fromAddress = new MailAddress("yourEmail@example.com", "Your Name");\n            MailAddress toAddress = new MailAddress("myFirstName.O'Reilly@mywebsite.com", "Recipient Name");\n            const string fromPassword = "yourEmailPassword";\n            const string subject = "Test Email";\n            const string body = "This is a test email.";\n\n            SmtpClient smtp = new SmtpClient\n            {\n                Host = "smtp.example.com", // Replace with your SMTP server\n                Port = 587, // Common port for SMTP\n                EnableSsl = true,\n                DeliveryMethod = SmtpDeliveryMethod.Network,\n                UseDefaultCredentials = false,\n                Credentials = new NetworkCredential(fromAddress.Address, fromPassword)\n            };\n\n            using (MailMessage message = new MailMessage(fromAddress, toAddress)\n            {\n                Subject = subject,\n                Body = body\n            })\n            {\n                smtp.Send(message);\n            }\n\n            Console.WriteLine("Email sent successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error sending email: " + ex.Message);\n        }\n    }\n}