using System;\nusing System.Diagnostics;\nusing System.Web.Hosting;\nusing System.Windows.Forms;\n\npublic class ApplicationTypeDetector\n{\n    public static string GetApplicationType()\n    {\n        // Check if running inside ASP.NET\n        if (HostingEnvironment.IsHosted)\n        {\n            return "ASP.NET";\n        }\n\n        // Check if running inside a Windows Service\n        using (var process = Process.GetCurrentProcess())\n        {\n            if (process.SessionId == 0)\n            {\n                return "Windows Service";\n            }\n        }\n\n        // Check if running as a Windows Forms application\n        if (Application.MessageLoop)\n        {\n            return "Windows Form";\n        }\n\n        // If none of the above, assume it is a Console application\n        if (Environment.UserInteractive)\n        {\n            return "Console";\n        }\n\n        return "Unknown";\n    }\n\n    public static void Main()\n    {\n        string appType = GetApplicationType();\n        Console.WriteLine($"Application Type: {appType}");\n    }\n}